import React, { useState } from 'react';

const Kafka = () => {
  const [activeFlow, setActiveFlow] = useState(null);
  const [selectedService, setSelectedService] = useState(null);

  const services = [
    {
      name: 'Order Service',
      role: 'Producer/Consumer',
      description: 'Creates orders and publishes OrderPlaced events',
      topics: { produces: ['order-events'], consumes: [] },
      color: 'bg-blue-100 border-blue-300'
    },
    {
      name: 'Payment Service',
      role: 'Producer/Consumer',
      description: 'Processes payments and publishes PaymentProcessed events',
      topics: { produces: ['payment-events'], consumes: ['order-events'] },
      color: 'bg-green-100 border-green-300'
    },
    {
      name: 'Inventory Service',
      role: 'Producer/Consumer',
      description: 'Updates stock and publishes InventoryUpdated events',
      topics: { produces: ['inventory-events'], consumes: ['order-events'] },
      color: 'bg-yellow-100 border-yellow-300'
    },
    {
      name: 'Shipping Service',
      role: 'Producer/Consumer',
      description: 'Initiates shipments and publishes ShipmentInitiated events',
      topics: { produces: ['shipping-events'], consumes: ['payment-events'] },
      color: 'bg-purple-100 border-purple-300'
    },
    {
      name: 'Notification Service',
      role: 'Consumer/Producer',
      description: 'Sends notifications and publishes EmailSent/SMSSent events',
      topics: { produces: ['notification-events'], consumes: ['order-events', 'payment-events', 'shipping-events'] },
      color: 'bg-pink-100 border-pink-300'
    }
  ];

  const topics = [
    { name: 'order-events', events: ['OrderPlaced', 'OrderCancelled'], color: 'bg-blue-50' },
    { name: 'payment-events', events: ['PaymentProcessed', 'PaymentFailed'], color: 'bg-green-50' },
    { name: 'inventory-events', events: ['StockUpdated', 'OutOfStock'], color: 'bg-yellow-50' },
    { name: 'shipping-events', events: ['ShipmentInitiated', 'ShipmentDelivered'], color: 'bg-purple-50' },
    { name: 'notification-events', events: ['EmailSent', 'SMSSent'], color: 'bg-pink-50' }
  ];

  const flowSteps = [
    { step: 1, service: 'Order Service', action: 'Customer places order → OrderPlaced event sent to order-events topic', active: false },
    { step: 2, service: 'Payment Service', action: 'Consumes OrderPlaced → Processes payment → Sends PaymentProcessed to payment-events', active: false },
    { step: 3, service: 'Inventory Service', action: 'Consumes OrderPlaced → Updates stock → Sends InventoryUpdated to inventory-events', active: false },
    { step: 4, service: 'Shipping Service', action: 'Consumes PaymentProcessed → Initiates shipment → Sends ShipmentInitiated to shipping-events', active: false },
    { step: 5, service: 'Notification Service', action: 'Consumes multiple events → Sends notifications → Publishes EmailSent/SMSSent', active: false }
  ];

  const handleFlowDemo = () => {
    setActiveFlow(0);
    flowSteps.forEach((_, index) => {
      setTimeout(() => {
        setActiveFlow(index);
      }, index * 1500);
    });

    setTimeout(() => {
      setActiveFlow(null);
    }, flowSteps.length * 1500);
  };

  return (
    <div className="max-w-7xl mx-auto p-6 bg-gray-50 min-h-screen">
      <div className="text-center mb-8">
        <h1 className="text-4xl font-bold text-gray-800 mb-4">Apache Kafka in E-commerce</h1>
        <p className="text-lg text-gray-600 max-w-3xl mx-auto">
          Event-driven microservice architecture enabling asynchronous, scalable communication between services
        </p>
      </div>

      {/* Core Concepts */}
      <div className="grid md:grid-cols-3 gap-6 mb-8">
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-xl font-semibold mb-4 text-blue-600">Core Components</h3>
          <ul className="space-y-2 text-gray-700">
            <li className="flex items-center"><span className="w-2 h-2 bg-blue-500 rounded-full mr-3"></span>Topics & Partitions</li>
            <li className="flex items-center"><span className="w-2 h-2 bg-blue-500 rounded-full mr-3"></span>Producers & Consumers</li>
            <li className="flex items-center"><span className="w-2 h-2 bg-blue-500 rounded-full mr-3"></span>Brokers & Zookeeper</li>
            <li className="flex items-center"><span className="w-2 h-2 bg-blue-500 rounded-full mr-3"></span>Consumer Groups</li>
          </ul>
        </div>

        <div className="bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-xl font-semibold mb-4 text-green-600">Key Features</h3>
          <ul className="space-y-2 text-gray-700">
            <li className="flex items-center"><span className="w-2 h-2 bg-green-500 rounded-full mr-3"></span>Distributed System</li>
            <li className="flex items-center"><span className="w-2 h-2 bg-green-500 rounded-full mr-3"></span>Fault Tolerance</li>
            <li className="flex items-center"><span className="w-2 h-2 bg-green-500 rounded-full mr-3"></span>High Throughput</li>
            <li className="flex items-center"><span className="w-2 h-2 bg-green-500 rounded-full mr-3"></span>Message Persistence</li>
          </ul>
        </div>

        <div className="bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-xl font-semibold mb-4 text-purple-600">Benefits</h3>
          <ul className="space-y-2 text-gray-700">
            <li className="flex items-center"><span className="w-2 h-2 bg-purple-500 rounded-full mr-3"></span>Loose Coupling</li>
            <li className="flex items-center"><span className="w-2 h-2 bg-purple-500 rounded-full mr-3"></span>Scalability</li>
            <li className="flex items-center"><span className="w-2 h-2 bg-purple-500 rounded-full mr-3"></span>Event-Driven Architecture</li>
            <li className="flex items-center"><span className="w-2 h-2 bg-purple-500 rounded-full mr-3"></span>Asynchronous Processing</li>
          </ul>
        </div>
      </div>

      {/* Interactive Flow Demo */}
      <div className="bg-white rounded-lg shadow-lg p-6 mb-8">
        <div className="flex justify-between items-center mb-6">
          <h3 className="text-2xl font-semibold text-gray-800">E-commerce Order Flow</h3>
          <button 
            onClick={handleFlowDemo}
            className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded-lg transition-colors"
          >
            Demo Flow
          </button>
        </div>

        <div className="space-y-4">
          {flowSteps.map((step, index) => (
            <div 
              key={index}
              className={`p-4 rounded-lg border-l-4 transition-all duration-500 ${
                activeFlow === index 
                  ? 'bg-blue-50 border-blue-500 transform scale-105' 
                  : 'bg-gray-50 border-gray-300'
              }`}
            >
              <div className="flex items-center">
                <div className={`w-8 h-8 rounded-full flex items-center justify-center text-white font-bold mr-4 ${
                  activeFlow === index ? 'bg-blue-500' : 'bg-gray-400'
                }`}>
                  {step.step}
                </div>
                <div>
                  <h4 className="font-semibold text-gray-800">{step.service}</h4>
                  <p className="text-gray-600">{step.action}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Services Overview */}
      <div className="grid lg:grid-cols-2 gap-8 mb-8">
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-2xl font-semibold mb-6 text-gray-800">Microservices</h3>
          <div className="space-y-4">
            {services.map((service, index) => (
              <div 
                key={index}
                className={`p-4 rounded-lg border-2 cursor-pointer transition-all hover:shadow-md ${service.color} ${
                  selectedService === index ? 'ring-2 ring-blue-400' : ''
                }`}
                onClick={() => setSelectedService(selectedService === index ? null : index)}
              >
                <div className="flex justify-between items-start">
                  <div>
                    <h4 className="font-semibold text-gray-800">{service.name}</h4>
                    <p className="text-sm text-gray-600 mt-1">{service.description}</p>
                    <span className="inline-block bg-gray-200 text-gray-700 text-xs px-2 py-1 rounded mt-2">
                      {service.role}
                    </span>
                  </div>
                  <div className="text-right">
                    <div className="text-xs text-gray-500">Produces: {service.topics.produces.length}</div>
                    <div className="text-xs text-gray-500">Consumes: {service.topics.consumes.length}</div>
                  </div>
                </div>

                {selectedService === index && (
                  <div className="mt-4 pt-4 border-t border-gray-300">
                    <div className="grid md:grid-cols-2 gap-4">
                      <div>
                        <h6 className="font-medium text-green-700 mb-2">Produces Topics:</h6>
                        {service.topics.produces.map((topic, i) => (
                          <span key={i} className="inline-block bg-green-100 text-green-800 text-xs px-2 py-1 rounded mr-2 mb-1">
                            {topic}
                          </span>
                        ))}
                      </div>
                      <div>
                        <h6 className="font-medium text-blue-700 mb-2">Consumes Topics:</h6>
                        {service.topics.consumes.map((topic, i) => (
                          <span key={i} className="inline-block bg-blue-100 text-blue-800 text-xs px-2 py-1 rounded mr-2 mb-1">
                            {topic}
                          </span>
                        ))}
                      </div>
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>

        <div className="bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-2xl font-semibold mb-6 text-gray-800">Kafka Topics</h3>
          <div className="space-y-4">
            {topics.map((topic, index) => (
              <div key={index} className={`p-4 rounded-lg border ${topic.color}`}>
                <h4 className="font-semibold text-gray-800 mb-2">{topic.name}</h4>
                <div className="flex flex-wrap gap-2">
                  {topic.events.map((event, i) => (
                    <span key={i} className="bg-white text-gray-700 text-sm px-3 py-1 rounded-full border">
                      {event}
                    </span>
                  ))}
                </div>
              </div>
            ))}
          </div>

          <div className="mt-6 p-4 bg-gray-100 rounded-lg">
            <h4 className="font-semibold text-gray-800 mb-2">Sample Event Structure</h4>
            <pre className="text-sm bg-gray-800 text-green-400 p-3 rounded overflow-x-auto">
{`{
  "orderId": "12345",
  "customerId": "789",
  "orderTotal": 100.50,
  "status": "PLACED",
  "timestamp": "2025-05-23T10:30:00Z"
}`}
            </pre>
          </div>
        </div>
      </div>

      {/* Implementation Examples */}
      <div className="bg-white rounded-lg shadow-lg p-6">
        <h3 className="text-2xl font-semibold mb-6 text-gray-800">Implementation Examples</h3>

        <div className="grid lg:grid-cols-2 gap-6">
          <div>
            <h4 className="font-semibold text-blue-600 mb-3">Producer Example (Order Service)</h4>
            <pre className="text-sm bg-gray-900 text-gray-100 p-4 rounded overflow-x-auto">
{`public void placeOrder(Order order) {
    // Order placement logic
    OrderPlacedEvent event = new OrderPlacedEvent(order);

    // Send to Kafka topic
    kafkaTemplate.send("order-events", event);
}`}
            </pre>
          </div>

          <div>
            <h4 className="font-semibold text-green-600 mb-3">Consumer Example (Payment Service)</h4>
            <pre className="text-sm bg-gray-900 text-gray-100 p-4 rounded overflow-x-auto">
{`@KafkaListener(topics = "order-events", 
               groupId = "payment-group")
public void processPayment(OrderPlacedEvent event) {
    // Payment processing logic
    PaymentProcessedEvent paymentEvent = 
        new PaymentProcessedEvent(event.getOrderId());

    kafkaTemplate.send("payment-events", paymentEvent);
}`}
            </pre>
          </div>
        </div>

        <div className="mt-6 p-4 bg-blue-50 rounded-lg">
          <h4 className="font-semibold text-blue-800 mb-2">Key Benefits of this Architecture:</h4>
          <ul className="text-blue-700 space-y-1">
            <li>• <strong>Loose Coupling:</strong> Services don't need to know about each other directly</li>
            <li>• <strong>Fault Tolerance:</strong> If one service fails, others continue processing</li>
            <li>• <strong>Scalability:</strong> Each service can be scaled independently</li>
            <li>• <strong>Event Sourcing:</strong> Complete audit trail of all system events</li>
            <li>• <strong>Real-time Processing:</strong> Events are processed as they occur</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Kafka;
