import React, { useState } from 'react';

const Kafka = () => {
  const [activeFlow, setActiveFlow] = useState(null);
  const [selectedService, setSelectedService] = useState(null);
  const [activeTab, setActiveTab] = useState('what');

  // Theory sections
  const theoryTabs = [
    { id: 'what', label: 'What is Kafka?' },
    { id: 'components', label: 'Kafka Components' },
    { id: 'workflow', label: 'Order Workflow' },
    { id: 'services', label: 'Microservices' },
  ];

  const theoryContent = {
    what: (
      <div className="space-y-4">
        <h3 className="text-xl font-semibold text-gray-800">Apache Kafka</h3>
        <p className="text-gray-700">
          Kafka is a <strong>distributed event streaming platform</strong> that enables:
        </p>
        <ul className="list-disc pl-6 space-y-2 text-gray-700">
          <li>Real-time data processing</li>
          <li>Asynchronous communication between services</li>
          <li>High-throughput, fault-tolerant message handling</li>
          <li>Event sourcing and stream processing</li>
        </ul>

        <div className="bg-blue-50 p-4 rounded-lg border border-blue-200 mt-4">
          <h4 className="font-semibold text-blue-800">Why Kafka for Microservices?</h4>
          <div className="grid grid-cols-2 gap-4 mt-2">
            <div>
              <p className="text-sm font-medium">Traditional Approach:</p>
              <ul className="text-xs text-gray-600 space-y-1 mt-1">
                <li>• Direct HTTP calls between services</li>
                <li>• Tight coupling</li>
                <li>• Synchronous communication</li>
                <li>• Hard to scale</li>
              </ul>
            </div>
            <div>
              <p className="text-sm font-medium">With Kafka:</p>
              <ul className="text-xs text-gray-600 space-y-1 mt-1">
                <li>• Events as communication</li>
                <li>• Loose coupling</li>
                <li>• Asynchronous processing</li>
                <li>• Easy scaling</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    ),
    components: (
      <div className="space-y-6">
        <h3 className="text-xl font-semibold text-gray-800">Core Kafka Components</h3>

        <div className="grid md:grid-cols-2 gap-4">
          <div className="bg-white p-4 rounded-lg border border-gray-200">
            <h4 className="font-semibold text-blue-800">1. Topics</h4>
            <p className="text-sm text-gray-700 mt-1">
              Categories/feeds where messages are stored (e.g., <code>order-events</code>)
            </p>
            <div className="mt-2 p-2 bg-gray-100 rounded">
              <p className="text-xs text-gray-600">Example Topics:</p>
              <ul className="text-xs text-gray-700 mt-1">
                <li>• order-events</li>
                <li>• payment-events</li>
                <li>• inventory-updates</li>
              </ul>
            </div>
          </div>

          <div className="bg-white p-4 rounded-lg border border-gray-200">
            <h4 className="font-semibold text-green-800">2. Partitions</h4>
            <p className="text-sm text-gray-700 mt-1">
              Topics are split into partitions for parallel processing
            </p>
            <div className="mt-2 p-2 bg-gray-100 rounded">
              <p className="text-xs text-gray-600">Key Properties:</p>
              <ul className="text-xs text-gray-700 mt-1">
                <li>• Ordered sequence of messages</li>
                <li>• Can be distributed across brokers</li>
                <li>• Enable horizontal scaling</li>
              </ul>
            </div>
          </div>

          <div className="bg-white p-4 rounded-lg border border-gray-200">
            <h4 className="font-semibold text-purple-800">3. Producers</h4>
            <p className="text-sm text-gray-700 mt-1">
              Services that publish messages to Kafka topics
            </p>
            <div className="mt-2 p-2 bg-gray-100 rounded">
              <p className="text-xs text-gray-600">Example:</p>
              <pre className="text-xs bg-gray-800 text-green-400 p-1 rounded mt-1">
{`orderService.publish(
  "order-events", 
  orderPlacedEvent
)`}
              </pre>
            </div>
          </div>

          <div className="bg-white p-4 rounded-lg border border-gray-200">
            <h4 className="font-semibold text-yellow-800">4. Consumers</h4>
            <p className="text-sm text-gray-700 mt-1">
              Services that read and process messages from topics
            </p>
            <div className="mt-2 p-2 bg-gray-100 rounded">
              <p className="text-xs text-gray-600">Example:</p>
              <pre className="text-xs bg-gray-800 text-green-400 p-1 rounded mt-1">
{`@KafkaListener(topics = "order-events")
void processOrder(OrderEvent event) {
  // Handle order
}`}
              </pre>
            </div>
          </div>

          <div className="bg-white p-4 rounded-lg border border-gray-200">
            <h4 className="font-semibold text-red-800">5. Brokers</h4>
            <p className="text-sm text-gray-700 mt-1">
              Kafka servers that store data and serve clients
            </p>
            <div className="mt-2 p-2 bg-gray-100 rounded">
              <p className="text-xs text-gray-600">Key Points:</p>
              <ul className="text-xs text-gray-700 mt-1">
                <li>• Form a Kafka cluster</li>
                <li>• Handle read/write requests</li>
                <li>• Replicate data for fault tolerance</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    ),
    workflow: (
      <div className="space-y-6">
        <h3 className="text-xl font-semibold text-gray-800">E-commerce Order Flow</h3>

        <div className="bg-white p-4 rounded-lg border border-gray-200">
          <div className="flex justify-between items-center mb-4">
            <h4 className="font-semibold text-blue-800">Order Processing Steps</h4>
            <button 
              onClick={() => {
                setActiveFlow(0);
                [1,2,3,4,5].forEach((step, i) => {
                  setTimeout(() => setActiveFlow(i), i * 1500);
                });
                setTimeout(() => setActiveFlow(null), 5 * 1500);
              }}
              className="bg-blue-600 text-white px-4 py-1 rounded text-sm"
            >
              Visualize Flow
            </button>
          </div>

          <div className="space-y-3">
            {[
              {
                step: 1,
                title: "Order Placement",
                description: "Customer places order → Order Service publishes OrderPlaced event",
                kafka: {
                  producer: "Order Service",
                  topic: "order-events",
                  message: `{ "eventType": "OrderPlaced", "orderId": "123" }`
                }
              },
              {
                step: 2,
                title: "Payment Processing",
                description: "Payment Service consumes OrderPlaced → processes payment → publishes PaymentProcessed",
                kafka: {
                  consumer: "Payment Service",
                  producer: "Payment Service",
                  topic: "payment-events",
                  message: `{ "eventType": "PaymentProcessed", "orderId": "123" }`
                }
              },
              {
                step: 3,
                title: "Inventory Update",
                description: "Inventory Service consumes OrderPlaced → updates stock",
                kafka: {
                  consumer: "Inventory Service",
                  topic: "order-events"
                }
              },
              {
                step: 4,
                title: "Shipping Initiation",
                description: "Shipping Service consumes PaymentProcessed → prepares shipment",
                kafka: {
                  consumer: "Shipping Service",
                  topic: "payment-events"
                }
              },
              {
                step: 5,
                title: "Customer Notification",
                description: "Notification Service consumes multiple events → sends emails/SMS",
                kafka: {
                  consumer: "Notification Service",
                  topics: ["order-events", "payment-events"]
                }
              }
            ].map((stepData, index) => (
              <div 
                key={index}
                className={`p-3 rounded-lg border-l-4 ${
                  activeFlow === index 
                    ? 'bg-blue-50 border-blue-500' 
                    : 'bg-gray-50 border-gray-300'
                }`}
              >
                <div className="flex items-start">
                  <div className={`w-6 h-6 rounded-full flex items-center justify-center text-white text-sm mr-3 mt-1 ${
                    activeFlow === index ? 'bg-blue-500' : 'bg-gray-400'
                  }`}>
                    {stepData.step}
                  </div>
                  <div>
                    <h5 className="font-medium text-gray-800">{stepData.title}</h5>
                    <p className="text-sm text-gray-600">{stepData.description}</p>

                    <div className="mt-2 text-xs">
                      {stepData.kafka.producer && (
                        <div className="mb-1">
                          <span className="font-medium">Producer:</span> {stepData.kafka.producer} → {stepData.kafka.topic}
                        </div>
                      )}
                      {stepData.kafka.consumer && (
                        <div>
                          <span className="font-medium">Consumer:</span> {stepData.kafka.consumer} ← {stepData.kafka.topics || stepData.kafka.topic}
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    ),
    services: (
      <div className="space-y-6">
        <h3 className="text-xl font-semibold text-gray-800">Microservices & Kafka Integration</h3>

        <div className="grid md:grid-cols-2 gap-4">
          {[
            {
              name: "Order Service",
              role: "Kafka Producer",
              description: "Creates orders and publishes events",
              topics: ["order-events"],
              kafkaComponents: [
                "Producer: Publishes to order-events",
                "Topic: order-events (3 partitions)",
                "Brokers: Connects to Kafka cluster"
              ],
              code: `// Publishing an order event
kafkaTemplate.send(
  "order-events", 
  orderId,
  new OrderPlacedEvent(order)
);`
            },
            {
              name: "Payment Service",
              role: "Producer & Consumer",
              description: "Processes payments by consuming order events",
              topics: ["order-events", "payment-events"],
              kafkaComponents: [
                "Consumer: order-events (group: payment-group)",
                "Producer: payment-events",
                "Brokers: Connects to Kafka cluster"
              ],
              code: `@KafkaListener(topics = "order-events")
public void processOrder(OrderEvent event) {
  // Process payment
  kafkaTemplate.send(
    "payment-events",
    paymentId,
    new PaymentProcessedEvent(payment)
  );
}`
            },
            {
              name: "Inventory Service",
              role: "Kafka Consumer",
              description: "Updates stock levels based on orders",
